mapper.select:
select

  distinct


,b.huodongleibie_name
from t_huodong a left join t_huodong_leibie b on a.huodong_leibie = b.huodongleibie_id




  order by ${orderByClause}


  limit #{startRow} , #{pageSize}

select

  distinct


,b.huodongleibie_name
from t_huodong a left join t_huodong_leibie b on a.huodong_leibie = b.huodongleibie_id right join t_huodong_canjia canjia on a.huodong_id = canjia.huodong_id




  order by ${orderByClause}


  limit #{startRow} , #{pageSize}

mapper.insert:
insert into t_huodong (huodong_id, huodong_name, huodong_leibie, 
  huodong_desc, huodong_place, huodong_producer, 
  huodong_fen, huodong_zhengming, huodong_time
  )
values (#{huodongId,jdbcType=INTEGER}, #{huodongName,jdbcType=VARCHAR}, #{huodongLeibie,jdbcType=INTEGER}, 
  #{huodongDesc,jdbcType=VARCHAR}, #{huodongPlace,jdbcType=VARCHAR}, #{huodongProducer,jdbcType=VARCHAR}, 
  #{huodongFen,jdbcType=DOUBLE}, #{huodongZhengming,jdbcType=VARCHAR}, #{huodongTime,jdbcType=TIMESTAMP}
  )
